import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import bgMusic from './assets/Taylor Swift - Style.mp3';

export default function App() {
  const [started, setStarted] = useState(false);
  const [timeLeft, setTimeLeft] = useState({ days:'--', hours:'--', minutes:'--', seconds:'--' });
  const [musicPlaying, setMusicPlaying] = useState(false);
  const [showAbout, setShowAbout] = useState(false);

  const audioRef = useRef(null);
  const confettiRef = useRef(null);
  const balloonsRef = useRef(null);
  const sparklesRef = useRef(null);

  // Countdown update
  useEffect(() => {
    if (!started) return;
    const target = new Date('2025-05-24T00:00:00');
    const timerId = setInterval(() => {
      const now = new Date();
      const diff = target - now;
      if(diff <= 0) {
        clearInterval(timerId);
        setTimeLeft({ days:'00', hours:'00', minutes:'00', seconds:'00' });
        return;
      }
      setTimeLeft({
        days: String(Math.floor(diff / (1000*60*60*24))).padStart(2,'0'),
        hours: String(Math.floor((diff / (1000*60*60)) % 24)).padStart(2,'0'),
        minutes: String(Math.floor((diff / (1000*60)) % 60)).padStart(2,'0'),
        seconds: String(Math.floor((diff / 1000)%60)).padStart(2,'0'),
      });
    }, 1000);
    return () => clearInterval(timerId);
  }, [started]);

  const launchConfetti = () => {
    if(!confettiRef.current) return;
    const colors = ['#FF9AD3','#FFA1A1','#FFB2E9', '#FDB9DB', '#FF6D91'];
    const container = confettiRef.current;
    container.innerHTML = '';
    for(let i=0; i<120; i++){
      const div = document.createElement('div');
      div.className = 'confetti-piece';
      div.style.backgroundColor = colors[i % colors.length];
      div.style.left = Math.random()*100 + 'vw';
      div.style.animationDuration = (3 + Math.random()*2) + 's';
      div.style.animationDelay = Math.random()*0.7 + 's';
      container.appendChild(div);
      div.addEventListener('animationend', () => div.remove());
    }
  };

  const createBalloon = (color, left, delay) => {
    if(!balloonsRef.current) return;
    const balloon = document.createElement('div');
    balloon.className = 'balloon balloon-animate';
    balloon.style.backgroundColor = color;
    balloon.style.left = left + 'vw';
    balloon.style.animationDelay = delay + 's';
    balloonsRef.current.appendChild(balloon);
    balloon.addEventListener('animationend', () => balloon.remove());
  }

  const launchBalloons = () => {
    const colors = ['#FF6D91', '#FFD3E0', '#FF8ABF', '#FFB6C1', '#FFC0CB'];
    for(let i=0; i<30; i++){
      createBalloon(
        colors[i % colors.length],
        5 + Math.random()*90,
        Math.random()*4
      );
    }
  };

  const createSparkle = (left, top, size, delay) => {
    if(!sparklesRef.current) return;
    const sparkle = document.createElement('div');
    sparkle.className = 'sparkle sparkle-animate';
    sparkle.style.left = left + 'vw';
    sparkle.style.top = top + 'vh';
    sparkle.style.width = sparkle.style.height = size + 'px';
    sparkle.style.animationDelay = delay + 's';
    sparklesRef.current.appendChild(sparkle);
    sparkle.addEventListener('animationend', () => sparkle.remove());
  }

  const launchSparkles = () => {
    for(let i=0; i<40; i++){
      createSparkle(
        Math.random()*100,
        Math.random()*100,
        4 + Math.random()*4,
        Math.random()*7
      );
    }
  }

  const start = () => {
    setStarted(true);
    launchConfetti();
    launchBalloons();
    launchSparkles();
    if(audioRef.current){
      audioRef.current.volume = 0.35;
      audioRef.current.play();
      setMusicPlaying(true);
    }
    setTimeout(() => setShowAbout(true), 5000);
  };

  const toggleMusic = () => {
    if(!audioRef.current) return;
    if(musicPlaying){
      audioRef.current.pause();
      setMusicPlaying(false);
    } else {
      audioRef.current.play();
      setMusicPlaying(true);
    }
  };

  return <>
    {/* Fixed countdown top */}
    <div className="countdown-top" aria-live="polite" aria-atomic="true" tabIndex={0}>
      <div className="countdown-label">Countdown to Sneha's 17th Birthday</div>
      <div className="countdown-timer">
        <span>{timeLeft.days}d</span> : <span>{timeLeft.hours}h</span> : <span>{timeLeft.minutes}m</span> : <span>{timeLeft.seconds}s</span>
      </div>
      {started && <button
        aria-label={musicPlaying ? 'Pause background music' : 'Play background music'}
        onClick={toggleMusic}
        className="music-toggle"
        aria-pressed={musicPlaying}
      >
        {musicPlaying ? '🔈 Pause Music' : '🔇 Play Music'}
      </button>}
    </div>
    <div className="app-container enhanced-bg">
      <div ref={confettiRef} className="confetti-wrapper" aria-hidden="true" />
      <div ref={balloonsRef} className="balloons-wrapper" aria-hidden="true" />
      <div ref={sparklesRef} className="sparkles-wrapper" aria-hidden="true" />

      {!started ? (
        <button className="magic-button" onClick={start} aria-label="Start the birthday surprise">
          🎁
        </button>
      ) : (
        <>
          <CakeAnimation side="left" />
          <CakeAnimation side="right" />

          <h1 className="main-title glow-text" tabIndex={0}>
            🎉 Happy 17th Birthday Sneha Pradhan! 🎉
          </h1>

          <div className="compliments-list" aria-live="polite" tabIndex={0}>
            {[
              'You’re not just a name, you’re a beautiful story unfolding 🌸',
              'Your smile brightens every room 🌞',
              'You care deeply for others 💖',
              'Your creativity is magical 🎨',
              'You always lift people up 🌈',
              'Your laugh is contagious 😄',
              "You're brilliantly smart 🧠",
              'You have a kind heart 💓',
              "You're beautifully unique ✨",
              "You're strong and courageous 🧘‍♀️",
              "You're an amazing friend 🤗",
              'Your energy is inspiring 🔥',
              'You make life magical 🌙',
            ].map((line, i) => (
              <p key={i} className="glow-text fade-in" style={{animationDelay: 1 + i * 0.3 + 's'}}>
                {line}
              </p>
            ))}
          </div>

          {showAbout && <AboutSneha />}
        </>
      )}
    </div>

    <audio ref={audioRef} loop preload="auto">
      <source src={bgMusic} type="audio/mpeg" />
      Your browser does not support the audio element.
    </audio>
  </>
}

function CakeAnimation({side}) {
  return (
    <div className={`cake-container cake-${side}`} aria-hidden="true" >
      <div className="cake-base">
        <div className="cake-layer cake-layer1"></div>
        <div className="cake-layer cake-layer2"></div>
        <div className="cake-layer cake-layer3"></div>
      </div>
      <CandleFire />
    </div>
  )
}

function CandleFire() {
  return (
    <div className="candle-flame" aria-hidden="true" >
      <div className="flame-core" />
      <div className="flame-glow" />
    </div>
  )
}

function AboutSneha() {
  return (
    <div className="about-sneha" tabIndex={0} role="region" aria-label="About Sneha Pradhan">
      <h2>About Sneha</h2>
      <p>
        Sneha Pradhan is a bright, creative, and kind soul who lights up every room with her radiant smile. As she turns 17, we celebrate her amazing journey and all the joy she brings to those around her. Here's to a year filled with magic and dreams come true!
      </p>
    </div>
  )
}
