import React, { useState } from 'react';
import BirthdayPage from './BirthdayPage';
import RiddleAndPuzzlePage from './RiddleAndPuzzlePage';
import Home from './Home';

function App() {
  // For example, keep track of which page to show
  const [page, setPage] = useState('Home');

  // Simple function to change pages (you can wire this up to buttons)
  const navigateTo = (pageName) => setPage(pageName);

  return (
    <div className="App">
      {/* Simple nav buttons for demo */}
      <nav>
        <button onClick={() => navigateTo('Home')}>Home</button>
        <button onClick={() => navigateTo('birthday')}>Birthday</button>
        <button onClick={() => navigateTo('riddle')}>Riddle & Puzzle</button>
      </nav>

      {/* Conditionally render pages */}
      {page === 'home' && <Home />}
      {page === 'birthday' && <BirthdayPage />}
      {page === 'riddle' && <RiddleAndPuzzlePage />}
    </div>
  );
}

export default App;
