import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import './animation.css';
import starsBg from './stars.gif';
import bgMusic from './assets/bg-music.mp3';

function App() {
  const [showContent, setShowContent] = useState(false);
  const [timeLeft, setTimeLeft] = useState({});
  const audioRef = useRef(null);

  // Timer Logic
  useEffect(() => {
    if (!showContent) return;

    const targetDate = new Date('2025-05-28T00:00:00'); // 28 May 12:00 AM
    const interval = setInterval(() => {
      const now = new Date();
      const difference = targetDate - now;

      const time = {
        days: Math.floor(difference / (1000 * 60 * 60 * 24)),
        hours: Math.floor((difference / (1000 * 60 * 60)) % 24),
        minutes: Math.floor((difference / (1000 * 60)) % 60),
        seconds: Math.floor((difference / 1000) % 60),
      };

      setTimeLeft(time);

      if (difference < 0) clearInterval(interval);
    }, 1000);

    return () => clearInterval(interval);
  }, [showContent]);

  const handleClick = () => {
    setShowContent(true);
    audioRef.current.play();
  };

  return (
    <div
      className="min-h-screen bg-black text-white flex flex-col items-center justify-center p-4"
      style={{
        backgroundImage: `url(${starsBg})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundAttachment: 'fixed',
      }}
    >
      {!showContent && (
        <button
          className="px-6 py-3 text-xl bg-red-600 hover:bg-red-700 rounded-full shadow-lg animate-pulse transition-all"
          onClick={handleClick}
        >
          Don‚Äôt Click It Sneha üëÄ
        </button>
      )}

      {showContent && (
        <>
          <h1 className="text-4xl md:text-6xl font-bold text-pink-400 mb-8 animate-bounce text-center drop-shadow-lg">
            Happy 17th Birthday, Sneha Pradhan! üéâ
          </h1>

          <div className="max-w-2xl text-lg space-y-4 text-center font-semibold animate-fade-in">
            <p className="glow">You‚Äôre not just a name, you‚Äôre a beautiful story unfolding üå∏</p>
            <p className="glow">Your smile brightens every room üåû</p>
            <p className="glow">You care deeply for others üíñ</p>
            <p className="glow">Your creativity is magical üé®</p>
            <p className="glow">You always lift people up üåà</p>
            <p className="glow">Your laugh is contagious üòÑ</p>
            <p className="glow">You're brilliantly smart üß†</p>
            <p className="glow">You have a kind heart üíì</p>
            <p className="glow">You're beautifully unique ‚ú®</p>
            <p className="glow">You're strong and courageous üßò‚Äç‚ôÄÔ∏è</p>
            <p className="glow">You're an amazing friend ü§ó</p>
            <p className="glow">Your energy is inspiring üî•</p>
            <p className="glow">You make life magical üåô</p>
          </div>

          <div className="mt-10 text-2xl font-bold text-yellow-400 animate-fade-in">
            Countdown to Your Next Birthday: <br />
            <span className="text-white">
              {timeLeft.days}d {timeLeft.hours}h {timeLeft.minutes}m {timeLeft.seconds}s
            </span>
          </div>
        </>
      )}

      <audio ref={audioRef} loop>
        <source src={bgMusic} type="audio/mpeg" />
        Your browser does not support the audio element.
      </audio>
    </div>
  );
}

export default App;
