import React, { useState, useEffect, useRef } from 'react';
import './App.css';//CSS Added
import bgMusic from './assets/Taylor Swift - Style.mp3';//Background Music Added
import photo from './assets/sneha.jpg';//Sneha's photo added
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import RiddleAndPuzzlePage from './RiddleAndPuzzlePage';

export default function App() {
  const [started, setStarted] = useState(false);
  const [timeLeft, setTimeLeft] = useState({ days:'--', hours:'--', minutes:'--', seconds:'--' });
  const [musicPlaying, setMusicPlaying] = useState(false);
  const [setShowAbout] = useState(false);
  const [balloons, setBalloons] = useState([]);
  const [countdownOver, setCountdownOver] = useState(false);
  const audioRef = useRef(null);
  const confettiRef = useRef(null);
  const sparklesRef = useRef(null);

  function HomePage({ continueText = "Continue" }) {
    const navigate = useNavigate();

  return (
    <div style={{ padding: 20, textAlign: 'center' }}>
      {/* Your existing homepage content here, or import it if big */}
      <h1>Welcome to Sneha's Birthday!</h1>
      {/* Other existing UI */}

      <button
        onClick={() => navigate('/riddle')}
        style={{
          fontSize: '1.2rem',
          padding: '12px 24px',
          marginTop: '30px',
          cursor: 'pointer',
          borderRadius: '25px',
          background: 'linear-gradient(135deg, #ff5ca1, #ff1a8c)',
          color: 'white',
          border: 'none',
          boxShadow: '0 0 30px #ff5ca1',
        }}
      >
        {continueText}
      </button>
    </div>
  );
}

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<HomePage continueText="Continue" />} />
        <Route path="/riddle" element={<RiddleAndPuzzlePage />} />
      </Routes>
    </Router>
  );
}

  // Countdown update
  useEffect(() => {
    const target = new Date('2025-05-25T00:00:00'); //change to 28
    const timerId = setInterval(() => {
      const now = new Date();
      const diff = target - now;
      if(diff <= 0) {
        clearInterval(timerId);
        setTimeLeft({ days:'00', hours:'00', minutes:'00', seconds:'00' });
        setCountdownOver(true); //mark countdown is over
        return;
      }
      setTimeLeft({
        days: String(Math.floor(diff / (1000*60*60*24))).padStart(2,'0'),
        hours: String(Math.floor((diff / (1000*60*60)) % 24)).padStart(2,'0'),
        minutes: String(Math.floor((diff / (1000*60)) % 60)).padStart(2,'0'),
        seconds: String(Math.floor((diff / 1000)%60)).padStart(2,'0'),
      });
    }, 1000);
    return () => clearInterval(timerId);
  }, [started]);

  //Creates and Launches Confetti
  const launchConfetti = () => {
    if(!confettiRef.current) return;

      const colors = ['#FF9AD3','#FFA1A1','#FFB2E9', '#FDB9DB', '#FF6D91'];

      const container = confettiRef.current;
        container.innerHTML = '';
    for(let i=0; i<600; i++){
      const div = document.createElement('div');
        div.className = 'confetti-piece';
        div.style.backgroundColor = colors[i % colors.length];
        div.style.top = (Math.random() * 20) + 'vh'; // random start between 0 and 20% viewport height
        div.style.left = Math.random()*100 + 'vw';
        div.style.animationDuration = (5 + Math.random() * 3) + 's'; // from 5 to 8 seconds
      const size = 5 + Math.random() * 7;// Random size between 5px and 12px
        div.style.width = size + 'px';
        div.style.height = size + 'px';
        div.style.animationDuration = (3 + Math.random() * 2) + 's';
        div.style.animationDelay = Math.random() * 0.7 + 's';
        container.appendChild(div);
        div.addEventListener('animationend', () => div.remove());
        div.style.animationDuration = (3 + Math.random()*2) + 's';
        div.style.animationDelay = Math.random()*0.7 + 's';
        container.appendChild(div);
        div.addEventListener('animationend', () => div.remove());
      }
  };

  //Creates and Launches Balloons
  const launchBalloons = () => {
    const colors = ['#FF69B4', '#FFD700', '#87CEFA', '#FFA07A', '#98FB98'];
    const newBalloons = Array.from({ length: 20 }, (_, i) => ({
      id: i,
      color: colors[Math.floor(Math.random() * colors.length)],
      left: Math.random() * 100,
      size: 40 + Math.random() * 30, // 40-70px
      delay: Math.random() * 2, // seconds
    }
    ));
    setBalloons(newBalloons);
  };

  //Creates and Launches Sparkles
  const launchSparkles = () => {
  if (!sparklesRef.current) return;

  const colors = ['#ffc0d8', '#ffd6e8', '#ffeaf5'];

  for (let i = 0; i < 60; i++) {
    const sparkle = document.createElement('div');
    sparkle.className = 'sparkle sparkle-animate';

    const left = Math.random() * 100;
    const top = Math.random() * 100;
    const size = 2 + Math.random() * 2;
    const delay = Math.random() * 7;

    sparkle.style.left = left + 'vw';
    sparkle.style.top = top + 'vh';
    sparkle.style.width = sparkle.style.height = size + 'px';
    sparkle.style.background = colors[Math.floor(Math.random() * colors.length)];
    sparkle.style.animationDelay = delay + 's';

    sparklesRef.current.appendChild(sparkle);

    sparkle.addEventListener('animationend', () => sparkle.remove());
  }
  };

  //Launches every function when button is clicked
  const start = () => {
    setStarted(true);
    launchConfetti();
    launchBalloons();
    launchSparkles();
    if(audioRef.current){
      audioRef.current.volume = .60;
      audioRef.current.play().catch(err => console.warn('Audio play failed:',err));
      setMusicPlaying(true);
    }
    setTimeout(() => setShowAbout(true), 5000);
  };

  //Toggle button for music
  const toggleMusic = () => {
    if(!audioRef.current) return;
    if(musicPlaying){
      audioRef.current.pause();
      setMusicPlaying(false);
    } else {
      audioRef.current.play();
      setMusicPlaying(true);
    }
  };

  return <>
    {/* Fixed countdown top */}
    <div className="countdown-top" aria-live="polite" aria-atomic="true" tabIndex={0}>
        <div className="countdown-label">Countdown to Sneha's 17th Birthday
        </div> {/* Countdown Label */}
        <div className="countdown-timer"> {/* Countdown Timer */}
          <span>{timeLeft.days}d</span> : <span>{timeLeft.hours}h</span> : <span>{timeLeft.minutes}m</span> : <span>{timeLeft.seconds}s</span>
        </div>
        {started && 
        <button aria-label={musicPlaying ? 'Pause background music' : 'Play background music'} onClick={toggleMusic} className="music-toggle" aria-pressed={musicPlaying}>
          {musicPlaying ? '🔊' : '🔇'}
        </button>} {/* Music Toggle Button */}
    </div>
    <div className="app-container enhanced-bg">{/* Running Confetti, Balloons, Sparkles */}
      <div ref={confettiRef} className="confetti-wrapper" aria-hidden="true" />
      <div className="balloons-wrapper">
        {started &&
          balloons.map((balloon) => (
          <div key={balloon.id} className="balloon-float" style={{left: `${balloon.left}%`, animationDelay: `${balloon.delay}s`, animationDuration: `${10 + Math.random() * 8}s`}}>
            <div className="tail" />
            <div className="balloon" style={{ backgroundColor: balloon.color, width: `${balloon.size}px`, height: `${balloon.size * 1.2}px`,}}>
          </div>
      </div>
      ))}
      </div>
    <div ref={sparklesRef} className="sparkles-wrapper" aria-hidden="true" />
    {!started ? 
      (
      <button className="magic-button" onClick={start} aria-label="Start the birthday surprise" disabled={!countdownOver} style={{ opacity: countdownOver ? 1 : 0.5, cursor: countdownOver ? 'pointer' : 'not-allowed' }}>
          <img src="gift.png" alt="Gift" className="gift-icon" />
        </button>
      ) : 
        (
          <>
            {/*<CakeAnimation side="right" /> {/* Creates a cake on the right side with 3 layers */}
            <h1 className="main-title glow-text" tabIndex={0}> {/* Makes the main title and adds glow */}
              🎉 Happy 17th Birthday Sneha !! 🎉
            </h1>
            <div className="about-section">
              <div className="poem" aria-live="polite" tabIndex={0}> {/* Makes the poem text and adds glow */}
              {[
                "It’s been just a year — not that long,",
                "But somehow it feels like we’ve been friends all along.",
                "You love your novels — Agatha’s the queen,",
                "Plot twists so wild, they belong on the screen.",
                "While I’m halfway lost by page twenty-two,",
                "You’re solving crimes like, “Too easy, boo.” 😎🔍",
                "Taylor Swift’s always looping in your mind,",
                "And every now and then, that one track plays...",
                <>You’ve got that look — <em>like you never go out of Style</em> 😉🎶</>,
                "And sleep? Oh please — your ride or die,",
                "If napping were a sport, you’d qualify. 😴💤",
                "Then there’s lab practicals, oh what a ride,",
                "Us pretending we’ve got reactions under control (we lied).",
                "Panic, notes, and that last-minute cram —",
                "But we passed, somehow — thank the exam.",
                "I’m just glad we met — honestly, truly,",
                "Class is better with you — fun and unruly.",
                "Best classmate ever — that’s you, not me. 💫"
              ].map((line, i) => (
                <p key={i} className="glow-text fade-in" style={{ animationDelay: 1 + i * 0.3 + 's' }}>
                  {line}
                </p> // Makes each string function as <p/> tag
                )
                )}
              </div>
              <div className="sneha-photo" tabIndex={0} role="region" aria-label="About Sneha Pradhan">
                  <img src={photo} alt="Sneha" style={{ width: '370px', height: 'auto', borderRadius: '10px' }}/> {/* Photo added  */}
              </div>
            </div>
          </>
        )
      }
      {!countdownOver && 
        (
          <p style={{ marginTop: '15px', fontSize: '1.5rem' , paddingTop: 30}}>{/* Runs when Countdown finishes */}
            ⏳ The gift unlocks when the countdown reaches zero!
          </p>
        )
      }
    </div>
    <audio ref={audioRef} loop preload="auto"> {/* Audio Fallback*/}
      <source src={bgMusic} type="audio/mpeg" />
      Your browser does not support the audio element.
    </audio>
  </>
}

