// App.js
import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import bgMusic from './assets/bg-music.mp3';

function App() {
  const [showContent, setShowContent] = useState(false);
  const [timeLeft, setTimeLeft] = useState({
    days: '--',
    hours: '--',
    minutes: '--',
    seconds: '--',
  });
  const [musicPlaying, setMusicPlaying] = useState(false);
  const audioRef = useRef(null);
  const confettiRef = useRef(null);

  useEffect(() => {
    if (!showContent) return;

    const targetDate = new Date('2025-05-28T00:00:00');
    const timerId = setInterval(() => {
      const now = new Date();
      const diff = targetDate - now;
      if (diff <= 0) {
        clearInterval(timerId);
        setTimeLeft({ days: '00', hours: '00', minutes: '00', seconds: '00' });
        return;
      }
      setTimeLeft({
        days: String(Math.floor(diff / (1000 * 60 * 60 * 24))).padStart(2, '0'),
        hours: String(Math.floor((diff / (1000 * 60 * 60)) % 24)).padStart(2, '0'),
        minutes: String(Math.floor((diff / (1000 * 60)) % 60)).padStart(2, '0'),
        seconds: String(Math.floor((diff / 1000) % 60)).padStart(2, '0'),
      });
    }, 1000);

    return () => clearInterval(timerId);
  }, [showContent]);

  const launchConfetti = () => {
    if (!confettiRef.current) return;
    const colors = ['#ff66b2', '#ffb3d9', '#ff99cc', '#ffcce6', '#ff80bf'];
    const numPieces = 120;
    const container = confettiRef.current;
    container.innerHTML = '';
    for (let i = 0; i < numPieces; i++) {
      const confetti = document.createElement('div');
      confetti.className = 'confetti-piece';
      confetti.style.backgroundColor = colors[Math.floor(Math.random() * colors.length)];
      confetti.style.left = `${Math.random() * 100}%`;
      confetti.style.animationDuration = `${(Math.random() * 3 + 3).toFixed(2)}s`;
      confetti.style.width = confetti.style.height = `${Math.random() * 8 + 6}px`;
      container.appendChild(confetti);
      confetti.addEventListener('animationend', () => confetti.remove());
    }
  };

  const handleStart = () => {
    setShowContent(true);
    launchConfetti();
    if (audioRef.current) {
      audioRef.current.volume = 0.35;
      audioRef.current.play();
      setMusicPlaying(true);
    }
  };

  const toggleMusic = () => {
    if (!audioRef.current) return;
    if (musicPlaying) {
      audioRef.current.pause();
      setMusicPlaying(false);
    } else {
      audioRef.current.play();
      setMusicPlaying(true);
    }
  };

  const playExtraAnimation = () => {
    launchConfetti();
    // Potential place for extra surprise animations
  };

  return (
    <div className="app-container enhanced-bg">
      <div className="floating-shapes"></div>

      <div className="content-wrapper">
        {!showContent ? (
          <button
            className="magic-button shimmering"
            onClick={handleStart}
            aria-label="Start the birthday surprise"
          >
            Donâ€™t Click It Sneha ðŸ‘€
          </button>
        ) : (
          <>
            <h1 className="main-title glow-text" tabIndex="0">
              ðŸŽ‰ Happy 17th Birthday Sneha Pradhan! ðŸŽ‰
            </h1>

            <div className="compliments-list" aria-live="polite">
              {[
                'Youâ€™re not just a name, youâ€™re a beautiful story unfolding ðŸŒ¸',
                'Your smile brightens every room ðŸŒž',
                'You care deeply for others ðŸ’–',
                'Your creativity is magical ðŸŽ¨',
                'You always lift people up ðŸŒˆ',
                'Your laugh is contagious ðŸ˜„',
                "You're brilliantly smart ðŸ§ ",
                'You have a kind heart ðŸ’“',
                "You're beautifully unique âœ¨",
                "You're strong and courageous ðŸ§˜â€â™€ï¸",
                "You're an amazing friend ðŸ¤—",
                'Your energy is inspiring ðŸ”¥',
                'You make life magical ðŸŒ™',
              ].map((line, i) => (
                <p
                  key={i}
                  className="glow-text fade-in"
                  style={{ animationDelay: `${1 + i * 0.3}s` }}
                  tabIndex={0}
                >
                  {line}
                </p>
              ))}
            </div>

            <div className="countdown-container fade-in" style={{ animationDelay: '5s' }}>
              <div className="countdown-label">Countdown to your next birthday:</div>
              <div className="countdown-timer" aria-live="polite" aria-atomic="true" tabIndex={0}>
                <span>{timeLeft.days}d</span> : <span>{timeLeft.hours}h</span> :{' '}
                <span>{timeLeft.minutes}m</span> : <span>{timeLeft.seconds}s</span>
              </div>
            </div>

            <div className="extra-buttons">
              <button className="secondary-button" onClick={playExtraAnimation} aria-label="Play surprise animation">
                Play Surprise Animation ðŸŽŠ
              </button>
              <button className="secondary-button" onClick={toggleMusic} aria-pressed={musicPlaying} aria-label={`${musicPlaying ? 'Pause' : 'Play'} background music`}>
                {musicPlaying ? 'Pause Music ðŸ”ˆ' : 'Play Music ðŸ”‡'}
              </button>
            </div>
          </>
        )}
      </div>

      <div ref={confettiRef} className="confetti-wrapper" aria-hidden="true" />
      <audio ref={audioRef} loop preload="auto">
        <source src={bgMusic} type="audio/mpeg" />
        Your browser does not support the audio element.
      </audio>
    </div>
  );
}

export default App;
