import React from 'react';
import './GamesPage.css';
import controllerImg from './assets/controller.png'; // Adjust path as needed
import { useNavigate } from 'react-router-dom';

const GamesPage = () => {
  const navigate = useNavigate();

  const gameButtons = [
    { label: 'Riddle', path: '/riddle' },
    { label: 'Puzzle', path: '/puzzle' },
  ];

  return (
    <div className="games-page">
      <h2 className="games-title">Choose a Game</h2>

      <div className="games-content">
        <img
          src={controllerImg}
          alt="Left Controller"
          className="controller controller-left"
        />

        <div className="games-button-container">
          {gameButtons.map((game, index) => (
            <button
              key={index}
              className="game-button"
              onClick={() => navigate(game.path)}
            >
              {game.label}
            </button>
          ))}
        </div>

        <img
          src={controllerImg}
          alt="Right Controller"
          className="controller controller-right"
        />
      </div>
    </div>
  );
};

export default GamesPage;
