import React, { useState } from 'react';
import './RiddleGame.css';

const riddleData = {
  question: "I speak without a mouth and hear without ears. I have no body, but I come alive with wind. What am I?",
  answer: "echo",
};

export default function RiddleGame() {
  const [input, setInput] = useState('');
  const [feedback, setFeedback] = useState('');

  const checkAnswer = () => {
    if (input.trim().toLowerCase() === riddleData.answer) {
      setFeedback('üéâ Correct! Well done!');
    } else {
      setFeedback('‚ùå Try again!');
    }
  };

  return (
    <div className="riddle-container">
      <h2 className="riddle-question">{riddleData.question}</h2>
      <input
        type="text"
        placeholder="Your answer"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        className="riddle-input"
      />
      <button onClick={checkAnswer} className="riddle-submit">Submit</button>
      {feedback && <p className="riddle-feedback">{feedback}</p>}
    </div>
  );
}
