import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

export default function Home() {
  const navigate = useNavigate();
  const [countdownOver, setCountdownOver] = useState(false);
  const [timeLeft, setTimeLeft] = useState({ days: '--', hours: '--', minutes: '--', seconds: '--' });

  useEffect(() => {
    const target = new Date(Date.now() + 10000); // 10 seconds from now for testing
    const timerId = setInterval(() => {
      const now = new Date();
      const diff = target - now;
      if (diff <= 0) {
        clearInterval(timerId);
        setTimeLeft({ days: '00', hours: '00', minutes: '00', seconds: '00' });
        setCountdownOver(true);
        return;
      }
      setTimeLeft({
        days: String(Math.floor(diff / (1000 * 60 * 60 * 24))).padStart(2, '0'),
        hours: String(Math.floor((diff / (1000 * 60 * 60)) % 24)).padStart(2, '0'),
        minutes: String(Math.floor((diff / (1000 * 60)) % 60)).padStart(2, '0'),
        seconds: String(Math.floor((diff / 1000) % 60)).padStart(2, '0'),
      });
    }, 1000);

    return () => clearInterval(timerId);
  }, []);

  return (
    <div>
      <div>
        Countdown: {timeLeft.days}d {timeLeft.hours}h {timeLeft.minutes}m {timeLeft.seconds}s
      </div>
      <button
        disabled={!countdownOver}
        onClick={() => countdownOver && navigate('/birthdaypage')}
      >
        {countdownOver ? 'Go to Birthday Page' : 'Wait for countdown'}
      </button>
    </div>
  );
}
